import React, { useState } from "react";

function Login() {
  const [showPassword, setShowPassword] = useState(false);
  const [usernameEmailValue, setUsernameEmailValue] = useState("");
  const [passwordValue, setPasswordValue] = useState("");
  const [inputType, setInputType] = useState("text");
  const [iconName, setIconName] = useState("person");

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleUsernameEmailChange = (event) => {
    const value = event.target.value;
    setUsernameEmailValue(value);

    const isEmail = value.includes("@"); // Verificar si la cadena contiene '@'
    setInputType(isEmail ? "email" : "text");
    setIconName(isEmail ? "mail" : "person");
  };

  const handlePasswordChange = (event) => {
    const value = event.target.value;
    setPasswordValue(value);
  };

  return (
    <div>
      <section className="background-container">
        <div className="login-box">
          <form action="">
            <h2>Iniciar Sesión</h2>
            <div className="input-box">
              <span className="icon">
                <ion-icon name={iconName}></ion-icon>
              </span>
              <input
                type={inputType}
                value={usernameEmailValue}
                onChange={handleUsernameEmailChange}
                required
              />
              <label>
                {inputType === "email"
                  ? "Correo Electrónico"
                  : "Nombre de Usuario"}
              </label>
            </div>
            <div className="input-box">
              <span className="icon">
                <ion-icon name="lock-closed"></ion-icon>
              </span>
              <input
                type={showPassword ? "text" : "password"}
                value={passwordValue}
                onChange={handlePasswordChange}
                required
              />
              <label>Contraseña</label>
              {passwordValue && (
                <button
                  type="button"
                  className="password-toggle"
                  onClick={togglePasswordVisibility}
                >
                  {showPassword ? (
                    <ion-icon name="eye-outline"></ion-icon>
                  ) : (
                    <ion-icon name="eye-off-outline"></ion-icon>
                  )}
                </button>
              )}
            </div>
            <div className="remember-forgot">
              <label>
                <input type="checkbox" />
                Recordarme
              </label>
              <a href="/forgotPassword">¿Olvidaste tu contraseña?</a>
            </div>
            <button type="submit">Iniciar Sesión</button>
            <div className="register-link">
              <p>
                ¿No tienes una cuenta? <a href="/register">Registrate</a>
              </p>
            </div>
          </form>
        </div>
      </section>
    </div>
  );
}

export default Login;
